public class MyScrollView extends NestedScrollView {

    private final String TAG = "MyScrollView";

    private int maxScrollHeight;

    public MyScrollView(Context context) {
        super(context);
    }

    public MyScrollView(Context context, AttributeSet attrs) {
        super(context, attrs);
    }

    public MyScrollView(Context context, AttributeSet attrs, int defStyleAttr) {
        super(context, attrs, defStyleAttr);
    }

    @Override
    protected void onMeasure(int widthMeasureSpec, int heightMeasureSpec) {
        super.onMeasure(widthMeasureSpec, heightMeasureSpec);

        //Ëé∑ÂèñscrollView ÊúÄÂ§ßÂèØÊªöÂä®ÁöÑË∑ùÁ¶ª
        maxScrollHeight = this.getChildAt(0).getMeasuredHeight() - this.getMeasuredHeight();
    }


    @Override
    public void onNestedPreScroll(@NonNull View target, int dx, int dy, @NonNull int[] consumed,
                                  int type) {

        super.onNestedPreScroll(target, dx, dy, consumed, type);

        //Â¶ÇÊûúÊúÄÂ§ßÂèØÊªöÂä®Ë∑ùÁ¶ªÂ§ß‰∫é 0 ÂàôË°®Á§∫ scrollview ÂèØ‰ª•ÊªöÂä®ÔºåÂàôÂéªÂÖàÊ∂àË¥πÁî®Êà∑ÊªëÂä®
        if(this.maxScrollHeight > 0){
            //Âà§Êñ≠Áî®Êà∑ÊòØÂê¶ÊòØÂêë‰∏äÊªëÂä®ÔºåÂπ∂‰∏îÊ≤°ÊúâË∂ÖÂá∫ scrollview ÂèØÊªëÂä®ÁöÑÊúÄÂ§ßË∑ùÁ¶ª
            boolean headerScrollUp = dy > 0 && getScrollY()  0;

            //Â¶ÇÊûú scrollview ÂèØ‰ª•ÊªëÂä®ÔºåÂàôÂéªÊ∂àË¥πÊªëÂä®
            if (headerScrollUp || headerScrollDown) {
                scrollBy(0, dy);
                consumed[1] = dy;
            }
            //ÈÄöÁü•Â≠êÂàóË°®ÔºåÁà∂Á∫ßÊ∂àË¥πÁöÑË∑ùÁ¶ª
            this.dispatchNestedPreScroll(dx, dy, consumed, null, type);
        }
    }
}

